import React, { useState } from "react";
import { PRODUCTS } from "../../products";
import { Product } from "./product";
import "./shop.css";
import FilterProduct from "../../filter/filterproduct";

export const Shop = (props) => {
  
  const [selectedFilters, setSelectedFilters] = useState([]);
  const [filteredPRODUCTS, setFilteredItems] = useState(PRODUCTS);

  let filters = ["1-р хоол", "2-р Хоол", "Уух зүйлс",];

  const handleFilterButtonClick = (selectedproductType) => {
    if (selectedFilters.includes(selectedproductType)) {
      let filters = selectedFilters.filter((el) => el !== selectedproductType);
      setSelectedFilters(filters);
    } else {
      setSelectedFilters([...selectedFilters, selectedproductType]);
    }
  };

  useEffect(() => {
    filterItems();
  }, [selectedFilters]);

  const filterItems = () => {
    if (selectedFilters.length > 0) {
      let tempItems = selectedFilters.map((selectedCategory) => {
        let temp = items.filter((item) => item.category === selectedCategory);
        return temp;
      });
      setFilteredItems(tempItems.flat());
    } else {
      setFilteredItems([...items]);
    }
  };

function onFilterValueSelected(filterValue){
  updateFilterText(filterValue);

}

  return (
    <div className="shop">
      <div className="shopTitle">
        <h1>Хоолны цэс</h1>
        {filters.map((productType, idx) => (
          <button
            onClick={() => handleFilterButtonClick(productType)}
            className={`button ${
              selectedFilters?.includes(productType) ? "active" : ""
            }`}
            key={`filters-${idx}`}
          >
            {category}
          </button>
          ))}
      </div>
      <FilterProduct filterValueSelected={onFilterValueSelected}> </FilterProduct>   
      <filteredList newProductList = {filteredList}> </filteredList>   
      <div className="products">
        {PRODUCTS.map((product) => (
          <Product data={product} />
        ))}
      </div>
    </div>
  );
};
