import React, { useContext } from "react";
import { ContactContext } from "../../context/Contact-context";

export const CartItem1 = (props) => {
  const { id, productName, price, productImage, description } = props.data;
  const { cartItems1, addToCart1, removeFromCart1, updateCartItemCount1 } =
    useContext(ContactContext);

  return (
    <div className="cartItem">
      <img src={productImage} />
      <div className="description">
        <p>
          <b>{productName}</b>
        </p>
        <p> Үнэ: {price}</p>
        <p> Орц: {description}</p>
        <div className="countHandler">
          <button onClick={() => removeFromCart1(id)}> - </button>
          <input
            value={cartItems1[id]}
            onChange={(e) => updateCartItemCount1(Number(e.target.value), id)}
          />
          <button onClick={() => addToCart1(id)}> + </button>
        </div>
      </div>
    </div>
  );
};
