import React, { useState } from 'react'
import Cards from 'react-credit-card'
import 'react-credit-card/es/styles-compiled.css'
import './creditcard.css'


function creditcard() {
    const [number, setNumber] =useState('')
    const [name, setName] =useState('')
    const [expiry, setExpiry] =useState('')
    const [cvc, setCvc] =useState('')
    const [focus, setFocus] =useState('')


  return (
    <div className='Credit-card'>
      <Cards number={number}
      name={name}
      expiry={expiry}
      cvc={cvc}
      focus={focus}/>
      <form>
        <input type='tel' name='number' placeholder='CardNumber' value={number} onChange={e =>setNumber(e.target.value)} onFocus={e => setFocus(e.target.name)}/>
        <input type='text' name='name' placeholder='Name' value={name} onChange={e =>setName(e.target.value)} onFocus={e => setFocus(e.target.name)}/>
        <input type='text' name='expiry' placeholder='MM/YY Expiry' value={expiry} onChange={e =>setExpiry(e.target.value)} onFocus={e => setFocus(e.target.name)}/>
        <input type='tel' name='cvc' placeholder='CVC' value={cvc} onChange={e =>setCvc(e.target.value)} onFocus={e => setFocus(e.target.name)}/>
        <input type='tel' name='number' placeholder='Focus' value={number} onChange={e =>(e.target.value)} onFocus={e => setFocus(e.target.name)}/>
      </form>
    
    
    
    </div>
  )
}

export default creditcard
