import axios from 'axios';
import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import Validation from './signupvalidation';


export const Signup = () => {
    const [ values, setValues] =useState({
        name: '',
        email: '',
        password: ''
      })
  return (
    <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
    <div className='bg-white p-3 rounded w-25'> 
    <h2>Sign-up</h2>
      <form action="" onSubmit={handleSubmit}>
        <div className='mb-3'>
          <label htmlFor='name'><strong>Нэр </strong></label>
          <input type="text" placeholder='Нэр оруулах' name='name' onChange={handleInput} className='form-control rounded-0'/>
          {errors.name && <span className='text-danger'>{errors.name}</span>}
          </div>
          <div className='mb-3'>
          <label htmlFor='email'><strong>Имэйл </strong></label>
          <input type="email" placeholder='Имэйл оруулах' className='form-control rounded-0'  name='email' onChange={handleInput} />
          {errors.email && <span className='text-danger'>{errors.email}</span>}
          </div>
          <div className='mb-3'>
            <label htmlFor='password'><strong>Нууц үг </strong></label>
            <input type='password' placeholder='Нууц үг оруулах'  name='password' onChange={handleInput} 
            className='form-control rounded-0' />
                {errors.password && <span className='text-danger'>{errors.password}</span>}
          </div>
          <button type='submit' className='btn btn-success w-100 rounded-0'>Signup</button>
          <p>Your agree terms</p>
          <Link to="/login" className='btn btn-default border w-100 bg-light rounded-0 text-decoration-none'>Log in</Link>
      </form>
    </div>
  </div>
  );
};
