import React, { useEffect, useState } from "react";
import { PRODUCTS } from "../../products";
import { Product } from "./product";
import "./shop.css";

export const Shop = () => {
  
  const [ selectedFilters, setSelectedFilters ] = useState([]);
  const [ filteredproducts, setFilteredproducts ] = useState(products);
  let filters = ["1-р Хоол", "2-р Хоол", "Уух зүйлс"];
  
  const handleFilterButtonClick = (selectedproductType) => {
    if (selectedFilters.includes(selectedproductType)) {
      let filters = selectedFilters.filter((el) => el !== selectedproductType);
      setSelectedFilters(filters);
    } else {
      setSelectedFilters([...selectedFilters, selectedproductType]);
    }
  };
  useEffect(() => {
    filterproducts();
  }, [selectedFilters]);

  const filterPRODUCTS = () => {
    if (selectedFilters.length > 0) {
      let tempproducts = selectedFilters.map((selectedproductType) => {
        let temp = products.filter((products) => products.setFilteredPRODUCTS === selectedproductType);
        return temp;
      });
      setFilteredPRODUCTS(tempPRODUCTSs.flat());
    } else {
      setFilteredPRODUCTS([...productss]);
    }
  };

  return (
    <div className="shop">
      <div className="shopTitle">
        <h1>Хоолны цэс</h1>
        
      <div className="filter-area">
        <select name="isAvailable" onChange={onFilterValueChanged}>
          <option value="1-р Хоол">1-р Хоол</option>
          <option value="2-р Хоол">2-р Хоол</option>
          <option value="Уух зүйлс">Уух зүйлс</option>
        </select>
      </div>
   
      </div>
     


      
     <div className="products">
        {PRODUCTS.map((product) => (
          <Product data={product} />
        ))}
      </div>
    </div>
  );
};
