import  { useEffect, useState } from "react";
import { PRODUCTS } from "../../products";
import { Product } from "./product";
import React, { useContext } from "react";
import { ShopContext } from "../../context/shop-context";

import "./shop.css";

function Shop(props) {
  const { id, productName, productType, price,  description, productImage } = props.data;
  const { addToCart, cartItems } = useContext(ShopContext);

  const cartItemCount = cartItems[id];


const  [data, setData ] = useState([]);
const [collection, setCollection ] = useState([]);

useEffect(()=>{
  setData(PRODUCTS);
  setCollection([... new Set(PRODUCTS.map((product) => product.productType))])
}, [])


const products_filter = (productData) => {
  const filterData = PRODUCTS.filter((product)=> product.productType == productData);
  setData(filterData);

}



  return (
    <div className="shop">
      <div className="shopTitle">
      <div className="bttns">
        <ul>
          <li><button onClick={()=> setData(PRODUCTS)}>Бүгд</button></li>
          {
            collection.map((product)=> <li><button onClick={()=>{products_filter(product)}}>{product}</button></li>)
          }
      </ul>
      <div className="products">
      {
            data.map((product) =><div className="cartItems"> 
            {/* <img src={product.productImage} key={product.id}/> */}
            <img src={productImage} />
            <h1>{productName}</h1>
            <p>Орц: {description}</p>
            <p>Үнэ: {price}</p>
            <button className="addToCartBttn" onClick={() => addToCart(product.id)}>
      Захиалах {cartItemCount > 0 && <> ({cartItemCount})</>}
      </button>
             </div> )
      }
      </div>
        
      </div>
      </div>
      {/* <img src={product.productImage} key={product.id}/> */}
      {/* <div className="products"> <img src={product.productImage} key={product.id}/>    
       */}
      {/*> */}
    </div>
  );
};
export default Shop;
