import React, { useContext, useState } from 'react'
import './form.css'
import { PRODUCTS } from '../products'
import { ShopContext } from '../context/shop-context'

const Form = () => {
    
    const { userData, submitData, setUserData } = useContext(ShopContext)

//     const [image, setImage] =useState('');
//     const [name, setName] =useState('');
//     const [price, setPrice] =useState('');
//     const [description, setDescription] =useState('');
//     const [type, setType] =useState('');
// const  [data, setData ] = useState([]);
// const [collection, setCollection ] = useState([]);






  return (
    <div className='foodsform'>
        <h2>Fileupload</h2>
        <form  >
            <label>Hoolni ner</label>
            <input type="text" name="name" value={userData['name']}  onchange={(e) =>setUserData({...userData, "name": e.target.value})} />
            <label>Hoolni une</label>
            <input type="number" name="price"  value={userData['price']}  onchange={(e) =>setUserData({...userData, "price": e.target.value})} />
            <label>Hoolni orts</label>
            <input type="text" name="description" value={userData['name']}  onchange={(e) =>setUserData({...userData, "description": e.target.value})} />
            <label>Hoolni zurag</label>
            <input  type="file" name="image" value={userData['Image']}  onchange={(e) =>setUserData({...userData, "Image": e.target.value})}/>
            <div>
            <label>Hoolni torol</label>
                <select type="text" name="type"value={userData['name']}  onchange={(e) =>setUserData({...userData, "name": e.target.value})} >
                    <option value="1-р хоол">1-р хоол</option>
                    <option value="2-р хоол">2-р хоол</option>
                    <option value="Уур зүйлс">3-р хоол </option>
                </select>
                            </div>
            <div>
                <button onClick={submitData}>add</button>
                <p>PRODUCTS:</p>
            </div>
        </form>
        <div>
            
            <button >edit</button>
            <button >del</button>
            <button >clear</button>
        </div>
    </div>
  )
}

export default Form
