import React from "react";
import { useState } from "react";
import './fileupload.css'
import  { PRODUCTS }  from '../products';

function FileUpload(){

    const [formValue, setFormValue ] = useState({productType:'',
productName:'', price:'', description:''});


const handleInput =(e) =>{
    const {name, value  } = e.target;
    setFormValue({...formValue, [name]: value});
}
const handleSubmit = async(e) =>{
    e.preventDefault();
    const allInputvalue = {  }
    console.log(allInputvalue);
}   
// productImage: formValue.zurg
// id: 6,
//     productType: "1-р хоол",
//     productName: "Ногооны зутан",
//     price: 14,
//     productImage: product6,
//     description: "Сонгино, үхрийн мах, халуун ногоо",



    return(
        <div className="formfood">
            <h2>Хоол нэмэх</h2>
            <form onChange={handleSubmit} >
                <label>Хоолны нэр</label>
                <input type="text" name="name" value={formValue.productName} onChange={handleInput}/>
                <label>Хоолны үнэ</label>
                <input type="text"  name="price" value={formValue.price} onChange={handleInput}/>
                <label>Хоолны орц</label>
                <input type="text" name="orts" value={formValue.description} onChange={handleInput}/>
                {/* <label> Хоолны зураг</label>
                <input type="File"  name="image" value={formValue.zurg}  onChange={handleInput}/> */}
                <select value={formValue.productType} onChange={handleInput}>
                    <option value="1-р хоол"> 1-р хоол</option>
                    <option value="2-р хоол">2-р хоол</option>
                    <option  value="Уух зүйлс"> Уух зүйлс</option>
                </select>
                <button type="submit">Upload</button>
            </form>
        </div>

    )
}
export default FileUpload
