import  { useEffect, useState } from "react";
import { PRODUCTS } from "../../products";
import { Product } from "./product";
import axios from "axios";


import "./shop.css";
import { useNavigate } from "react-router-dom";

function Shop() {

const [user, setUser] = useState(PRODUCTS);
useEffect(() => {
  axios.get('http://localhost:8081')
  .then(res=> setUser(res.data))
  .catch(err => console.log(err));
},[])







const  [data, setData ] = useState([]);
const [collection, setCollection ] = useState([]);

useEffect(()=>{
   setData(PRODUCTS);
  setCollection([... new Set(PRODUCTS.map((product) => product.productType))])
}, [])


const products_filter = (productData) => {
  const filterData = PRODUCTS.filter((product)=> product.productType == productData);
  setData(filterData);

}
  return (



    <div className="shop">
      <div className="shopTitle">
      <div className="bttns">
  
        <ul>
          <li><button onClick={()=> setData(PRODUCTS)}>Бүгд</button></li>
          {
            collection.map((product)=> <li><button onClick={()=>{products_filter(product)}}>{product}</button></li>)
          }
      </ul>
   
      <div className="products">
      {
            data.map((product) =><div className="cart"> 
            <Product data={product}/>
           </div> )
      }
      <div className="d-flex vh-100 bg-white">
        <div >
            <h2 className="column text-align-center">Хоол</h2>
            <div>
                <Link to="/create" className="btn btn-success">Нэмэх + </Link>
            </div>
            <table className="table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Хоолны нэр</th>
                        <th>Үнэ</th>
                        <th>Хоолны орц</th>
                        <th>Зураг</th>
                        <th>Хоолны төрөл</th>
                        <th>Үйлдэл</th>
                    </tr>
                </thead>
                <tbody>
                {data.map((product, index) => {
                    return <tr key={index}>
                        <td>{product.id}</td>
                        <td>{product.productName}</td>
                        <td>{product.price}</td>
                        <td>{product.description}</td>
                        <td>{product.productImage}</td>
                        <td>{product.productType}</td> 
                         <td>
                            <Link  to={`/read/${product.id}`} className="btn btn-sm btn-info">Харах</Link>
                            <Link to={`/edit/${product.id}`}className="btn btn-sm btn-primary mx-2">Засах</Link>
                            <button onClick={ () => handleDelete(product.id)} className="btn btn-sm btn-danger">Устгах</button>
                         </td>
                         </tr>
                })},
                </tbody>
            </table>
        </div>
    </div>
      
        
      </div>
      </div>
      </div>
      </div>
      
  );
};
export default Shop;
