import React, { useEffect, useState } from "react";
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css'
import { PRODUCTS } from "../products";
import { Link } from "react-router-dom";


function Form() {
    const [data, setData] = useState(PRODUCTS);
    useEffect(() => {
        axios.get('http://localhost:8081/')
        .then(res => setData(res.data))
        .catch(err => console.log(err));
    },[])

const handleDelete = (id) => {
    axios.delete('http://localhost:8081/delete/'+id)
    .then(res => {
        location.reload();
    })
    .catch(err => console(err))
}
  

  return (
    <div>
        <div >
            <h2>Hool</h2>
            <div>
                <Link to="/create" className="btn btn-success">Create + </Link>
            </div>
            <table className="table">
                <thead>
                    <tr>
                        <th>id</th>
                        <th>Name</th>
                        <th>price</th>
                        <th>description</th>
                        <th>image</th>
                        <th>type</th>
                        <th>action</th>
                    </tr>
                </thead>
                <tbody>
                {data.map((product, index) => {
                    return <tr key={index}>
                        <td>{product.id}</td>
                        <td>{product.productName}</td>
                        <td>{product.price}</td>
                        <td>{product.description}</td>
                        <td>{product.productImage}</td>
                        <td>{product.productType}</td> 
                         <td>
                        <Link  to={`/read/${product.id}`} className="btn btn-sm btn-info">Read</Link>
                            <Link to={`/edit/${product.id}`}className="btn btn-sm btn-primary mx-2">Edit</Link>
<button onClick={ () => handleDelete(product.id)} className="btn btn-sm btn-danger">Delete</button>
                         </td>
                         </tr>
                })}
                </tbody>
            </table>
        </div>
    </div>
  )
}

export default Form
