import React, { useEffect, useState } from "react";
import { PRODUCTS } from "../../products";
import { Product } from "./product";
import "./shop.css";

export const Shop = (props) => {

  const [ selectedFilters, setSelectedFilters ] = useState([]);
  const [ filteredPRODUCTS, setFilteredPRODUCTS ] = useState(PRODUCTS);
  const filters = [ "1-р хоол", "2-р Хоол", "Уух зүйлс" ];


  const handleFilterButtonClick = (selectedproductType) => {
    if (selectedFilters.includes(selectedproductType)) {
      let filters = selectedFilters.filter((el) => el !== selectedproductType);
      setSelectedFilters(filters);
    } else {
      setSelectedFilters([...selectedFilters, selectedproductType]);
    }
  };

  useEffect(() => {
    filteredPRODUCTS();
  }, [selectedFilters]);

  const filterItems = () => {
    if (selectedFilters.length > 0) {
      let tempItems = selectedFilters.map((selectedCategory) => {
        let temp = items.filter((item) => item.category === selectedCategory);
        return temp;
      });
      setFilteredItems(tempItems.flat());
    } else {
      setFilteredItems([...items]);
    }
  };
  
  return (
    <div>
    <div className="buttons-container">
      {filters.map((category, idx) => (
        <button
          onClick={() => handleFilterButtonClick(category)}
          className={`button ${
            selectedFilters?.includes(category) ? "active" : ""
          }`}
          key={`filters-${idx}`}
        >
          {category}
        </button>
      ))}
    </div>
    {/* // <div className="shop">
    //   <div className="shopTitle">
    //   <h1>Хоолны цэс</h1>
    //   <div className="bttns">
    //     <button value="1-р хоол" onClick={()=>item.productType}>1-р Хоол</button>
    //     <button value="2-р хоол" onClick={()=>item.productType}> 2-р Хоол</button>
    //     <button value="Уух зүйлс" onClick={()=>item.productType}>Уух зүйлс</button>
    //   </div>
    //   </div> */}
      
      <div className="products">
        {PRODUCTS.map((product) => (
          <Product data={product} />
        ))}
      </div>
    </div>
  );
};
