import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

function signup() {
  const [values, setValues] = useState({
    name: "",
    email: "",
    password: "",
  });
  const navigate = useNavigate();

  const handleInput = (event) => {
    setValues((prev) => ({
      ...prev,
      [event.target.name]: [event.target.values],
    }));
};

const handleSubmit = async (event) => {
    event.preventDefault();
    axios.post('http://localhost:8787/signup', values)
    .then(res => 
        { console.log(res) 
        navigate('/')})
    .catch(err => console.log(err));
}

  return (
    <div className="d-flex justify-content-center align-items-center bg-primary vh-100">
      <div className="bg-white p-3 rounded w-3">
        <h2>Sign-up</h2>
        <form>
          <div className="mb-3">
            <label htmlFor="name">
              <strong>Username</strong>
            </label>
            <input
              type="text"
              placeholder="Enter Username"
              name="name"
              onChange={handleInput}
              className="form-control rounded -0"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="email">
              <strong>Email</strong>
            </label>
            <input
              type="text"
              placeholder="Enter Email"
              name="name"
              onChange={handleInput}
              className="form-control rounded -0"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="password">
              <strong>Password</strong>
            </label>
            <input
              type="text"
              placeholder="Enter Password"
              name="name"
              onChange={handleInput}
              className="form-control rounded -0"
            />
          </div>
          <button type="submit" className="btn btn-success w-100 rounded-0">
            Sign up{" "}
          </button>
          <p>hisdhasdsadsadsa</p>
          <Link
            to="/login"
            className="btn btn-default border w-100 bg-light rounded-0 text-decoration underline"
          ></Link>
        </form>
      </div>
    </div>
  );
}

export default signup;
