import React from "react";
import { useState } from "react";
import './fileupload.css'
import  { PRODUCTS }  from '../products';

function FileUpload(){


    const [ data, setData ] = useState('')
    const [collection, setCollection ] = useState([]);
    const [productType, setType] = useState('');
    const [productName, setName] = useState('');
    const [Price, setPrice] = useState('');
    const [productImage, setImage] = useState('');
    const [description, setDes ] = useState('');

    useEffect(()=>{
        setData(PRODUCTS);
        setCollection([... new Set(PRODUCTS.map)])
      }, [])

      const products_filter = (productData) => {
        const filterData = PRODUCTS.filter(()=> PRODUCTS == productData);
        setData(filterData);
      
      }



    const handleSubmit = (e) =>{
        e.preventDefault();
    }

    return(
        <div className="formfood">
            <h2>Хоол нэмэх</h2>
            <form  onSubmit={handleSubmit}>
                <label>Хоолны нэр</label>
                <input type="text" value={productName} name="Name" onChange={(e) =>setName(e.target.value)} />
                <label>Хоолны үнэ</label>
                <input type="text"  value={Price} name="price" onChange={(e) =>setPrice(e.target.value)} />
                <label>Хоолны орц</label>
                <input type="text" value={description} name="description"  onChange={(e) =>setDes(e.target.value)}/>
                <label> Хоолны зураг</label>
                <input type="File" value={productImage} name="image" onChange={(e) =>setImage(e.target.value)} />
                <select>
                    <option value={productType} onChange={(e) =>setType(e.target.value)}> 1-р хоол</option>
                    <option value={productType}  onChange={(e) =>setType(e.target.value)}>2-р хоол</option>
                    <option value={productType}  onChange={(e) =>setType(e.target.value)}> Уух зүйлс</option>
                </select>
                <button onClick={PRODUCTS}>Upload</button>
            </form>
        </div>

    )
}
export default FileUpload
