const [values, setValues ] = useState({
  name: '',
  price: '',
  description: '',
  type: '',
  image:''
})
const navigate = useNavigate();
const handleSubmit =(e) => {
  e.preventDefault();
  axios.post('http://localhost:8081/food', values)
  .then(res =>{
       console.log(res);
       navigate('/')        
})
.catch(err => console.log(err))
}




  return ( 
    <div className='form'>
        <div className='container'>
            <form className='form-control' onSubmit={handleSubmit}>
                <h2>Hool nemeh</h2>
                    <label htmlFor=''>Name</label>
                    <input type="text" placeholder='enter name' className='name' onChange={e => setValues({...values, Feedback: e.target.value})}/>         
                    <label htmlFor=''>Price</label>
                    <input type="text" placeholder='enter price ' className='price' onChange={e => setValues({...values, email: e.target.value})}/>           
                    <label htmlFor=''>Description</label>
                    <input type="text" placeholder='enter description' className='description' onChange={e => setValues({...values, description: e.target.value})}/>    
                 <button  className="btn btn-success">Upload</button>
            </form>
          
        </div>
    </div>
  )
}

export default Feedback;
