import React, { useState } from "react";
import { PRODUCTS } from "../../products";
import { Product } from "./product";
import "./shop.css";
import FilterProduct from "../../filter/filterproduct";

export const Shop = (props) => {
  
let [ filterproductType, updateFilterText] = useState("1-р Хоол");
let filteredList = PRODUCTS.filter((PRODUCTS)=>{
if (filterproductType === '1-р хоол'){
  return PRODUCTS.productType ='1-р хоол' === true;
} else if (filterproductType === '2-р хоол'){
  return PRODUCTS.productType === false;
} else  if (filterproductType === 'Уух зүйлс'){
    return PRODUCTS.isavailable === true 
  }
})

function onFilterValueSelected(filterValue){
  updateFilterText(filterValue);

}

  return (
    <div className="shop">
      <div className="shopTitle">
        <h1>Хоолны цэс</h1>
      </div>
      <FilterProduct filterValueSelected={onFilterValueSelected}> </FilterProduct>      
      <div className="products">
        {PRODUCTS.map((product) => (
          <Product data={product} />
        ))}
      </div>
    </div>
  );
};
