import React, { useEffect, useState } from "react";
import { PRODUCTS } from "../../products";
import { Product } from "./product";
import "./shop.css";

export const Shop = (props) => {


  const [ data, setData] = useState([]);
  const [ collection,setCollection ] = useState([]);

  useEffect(()=> {
    setData(PRODUCTS);
    setCollection([... new Set(PRODUCTS.map((item) => item.productType))])
  },[])


  return (
    <div className="shop">
      <div className="shopTitle">
      <h1>Хоолны цэс</h1>
      <div className="bttns">
        <button value="1-р хоол" onClick={()=>item.productType}>1-р Хоол</button>
        <button value="2-р хоол" onClick={()=>item.productType}> 2-р Хоол</button>
        <button value="Уух зүйлс" onClick={()=>item.productType}>Уух зүйлс</button>
      </div>
      </div>
      
      <div className="products">
        {PRODUCTS.map((product) => (
          <Product data={product} />
        ))}
      </div>
    </div>
  );
};
