import React, { useEffect, useState } from "react";
import { PRODUCTS } from "../../products";
import { Product } from "./product";
import "./shop.css";

export const Shop = (props) => {

  const [productType, setproductType] = useState('');



  return (
    <div className="shop">
      <div className="shopTitle">
      <h1>Хоолны цэс</h1>
      <div className="bttns">
        <button value="1-р хоол" onClick={(e) = setproductType(e.target.value)}>1-р Хоол</button>
        <button value="2-р хоол" onClick={(e) = setproductType(e.target.value)} > 2-р Хоол</button>
        <button value="Уух зүйлс"onClick={(e) = setproductType(e.target.value)}>Уух зүйлс</button>
      </div>
      </div>
      
      <div className="products">
        {PRODUCTS.map((product) => (
          <Product data={product} />
        ))}
      </div>
    </div>
  );
};
