import React, { useEffect, useState } from "react";
import { PRODUCTS } from "../../products";
import { Product } from "./product";
import "./shop.css";

export const Shop = () => {

const  [data, setData ] = useState([]);
const [collection, setCollection ] = useState([]);

useEffect(()=>{
  setData(PRODUCTS);
  setCollection([... new Set(PRODUCTS.map((product) => product.productType))])
}, [])


const productss_filter = (productData) => {
  const filterData = PRODUCTS.filter((product)=> product.productType == productData);
  setData(filterData);

}



  return (
    <div className="shop">
      <div className="shopTitle">
      <div className="bttns">
        <ul>
          <li><button>Бүгд</button></li>
          {
            collection.map((product) =><li><button onClick={()=>{productss_filter(product)}}>{product}</button></li>)
          }
        </ul>
      </div>
      </div>
      
      <div className="products">
        {PRODUCTS.map((product) => (
          <Product data={product} />
        ))}
      </div>
    </div>
  );
};
