import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { Validation } from './signupvalidation';
import axios from 'axios'

export const Signup =() =>  {
    const [ values, setValues] =useState({
        name: '',
        email: '',
        password: ''
      })
      const navigate = useNavigate();
      const [errors, setErrors] = useState({})
      const handleInput = (event) => {
        setValues(prev => ({...prev,[event.target.name]: [event.target.values]}))
      }
      const handleSubmit = (event) => {
        event.preventDefault();
        setErrors(Validation(values));
        if(errors.name === "" && errors.email === "" && errors.password === ""){
            axios.post('http://localhost:8787/signup', values)
            .then(res =>  {
              navigate('/signup');
            })
            .catch(err => console.log(err));
        }
      }
  return (
    <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
    <div className='bg-white p-3 rounded w-25'> 
    <h2>Sign-up</h2>
      <form action="" onSubmit={handleInput}>
        <div className='mb-3'>
          <label htmlFor='name'><strong>Name </strong></label>
          <input type="text" placeholder='Enter Name' name='name' onChange={handleInput} className='form-control rounded-0'/>
          {errors.name && <span className='text-danger'>{errors.name}</span>}
          </div>
          <div className='mb-3'>
          <label htmlFor='email'><strong>Email </strong></label>
          <input type="email" placeholder='Enter Email' className='form-control rounded-0'  name='email' onChange={handleInput} />
          {errors.email && <span className='text-danger'>{errors.email}</span>}
          </div>
          <div className='mb-3'>
            <label htmlFor='password'><strong>Password</strong></label>
            <input type='password' placeholder='Enter Password'  name='password'onChange={handleInput} 
            className='form-control rounded-0' />
                {errors.password && <span className='text-danger'>{errors.password}</span>}
          </div>
          <button type='submit' className='btn btn-success w-100 rounded-0'>Signup</button>
          <Link to="/" className='btn btn-default border w-100 bg-light rounded-0 text-decoration'></Link>
      </form>
    </div>
  </div>
  );
};

export default Signup;
