import React, { Component } from 'react'
import  ProductConsumer, {ProductProvider}  from './Context'
import { Table, Button } from 'phosphor-react'


export default class homem extends Component {
  render() {
    return (
      <div className='container'>
        <h1>Crud</h1>
        <ProductConsumer>
            {(value) => {
                return(
                    <Table size="sm" variant="dark" striped bordered hover >
                        <tbody>
                            <tr>
                                <th>Хоолны нэр</th>
                                <th>Хоолны үнэ</th>
                                <th>Хоолны орц</th>
                                <th>Хоолны зураг</th>
                            </tr>
                            {value.Alldata.map(product => {
                                return(
                                    <tr>
                                        <td>{product.productName}</td>
                                        <td>{product.price}</td>
                                        <td>{product.description}</td>
                                        <td>{product.productImage}</td>
                                    </tr>
                                )

                            })}
                            </tbody>
                            </Table>
                       )
            }}
        </ProductConsumer>
        

      </div>
    )
  }
}
