import React, { useState } from "react";

export default function Feedback () {


  const [modal, setModal] = useState(false)
  return(
    <div className="review"> 
      <h1 className="h1">Feedback form</h1>
      <button  className="btn btn-green" onClick={()=> setModal((value) => value) }>Toogle Feedback</button>
      <div className="Feedback">
      {modal && ( <form>
          <input placeholder="Name" name="name"/>        
              <input placeholder="email" name="email"/>
            <input placeholder="Feedback" name="feedback"/>
          <button className="btn btn-grey">Submit feedback</button>
      </form>)}

      </div>
    </div>
  )
}
