import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { PRODUCTS } from '../products';

function Create() {
    
    const [authors, setAuthors] = useState(PRODUCTS)
useEffect(() => {
    const fetchData = async () => {
    const result = await fetch('http://localhost:3001/authors')
    }
}, [])


    const [values, setValues ] = useState({
        name: '',
        price: '',
        description: '',
        type: '',
        image: '',
    })

    const navigate = useNavigate();
    const handleSubmit =(e) => {
        e.preventDefault();
        axios.post('http://localhost:8081/food', values)
        .then(res =>{
             console.log(res);
             navigate('/')        
    })
    .catch(err => console.log(err))
}



  return (
    <div className='d-flex vh-100  justify-content-center align-items-center'>
        <div className='w-50 bg-white rounded p-3'>
            <form onSubmit={handleSubmit}>
                <h2>Hool nemeh</h2>
                 <div className='mb-2'>
                    <label htmlFor=''>Name</label>
                    <input type="text" placeholder='enter name' className='form-control' onChange={e => setValues({...values, name: e.target.value})}/>
                 </div>
                 <div className='mb-2'>
                    <label htmlFor=''>Price</label>
                    <input type="number" placeholder='enter price ' className='form-control' onChange={e => setValues({...values, price: e.target.value})}/>
                 </div>
                 <div className='mb-2'>
                    <label htmlFor=''>Description</label>
                    <input type="text" placeholder='enter description' className='form-control ' onChange={e => setValues({...values, description: e.target.value})}/>
                 </div>
                 <div className='mb-2'>
                    <label htmlFor=''>type</label>
                    <select type="text" placeholder='enter type' className='form-control' onChange={e => setValues({...values, type: e.target.value})}>
                        <option  value='1-р хоол'>1-р Хоол</option>
                        <option value='2-р хоол'>2-р Хоол</option>
                        <option value='Уух зүйлс'>Уух зүйлс</option>
                    </select>
                 </div>
                 <div className='mb-2'>
                    <label htmlFor=''>image</label>
                    <input type="file" placeholder='enter image' className='form-control' onChange={e => setValues({...values, image: e.target.value})}/>
                 </div>
                 <button  className="btn btn-success" type='submit'>Upload</button>
            </form>
          
        </div>
    </div>
  )
}

export default Create;
