import React from "react";
import { useState } from "react";
import './fileupload.css'
import  { PRODUCTS }  from '../products';

function FileUpload(){

    const [formValue, setFormValue ] = useState({name:"",une:"", orts:"",zurg:""})


const handleInput =(e) =>{
    const {name, value  } = e.target;
}



    return(
        <div className="formfood">
            <h2>Хоол нэмэх</h2>
            <form onChange={handleSubmit} >
                <label>Хоолны нэр</label>
                <input type="text" name="name" value={formValue.name} onChange={handleInput}/>
                <label>Хоолны үнэ</label>
                <input type="text"  name="une" value={formValue.une} onChange={handleInput}/>
                <label>Хоолны орц</label>
                <input type="text" name="orts" value={formValue.orts} onChange={handleInput}/>
                <label> Хоолны зураг</label>
                <input type="File"  name="image" value={formValue.zurg}  onChange={handleInput}/>
                <select>
                    <option name="type"> 1-р хоол</option>
                    <option name="type" >2-р хоол</option>
                    <option  name="type"> Уух зүйлс</option>
                </select>
                <button onClick={PRODUCTS}>Upload</button>
            </form>
        </div>

    )
}
export default FileUpload
