import React, { useContext, useState } from 'react'
import './form.css'
import { PRODUCTS } from '../products'

const Form = () => {
    

    const [image, setImage] =useState('');
    const [name, setName] =useState('');
    const [price, setPrice] =useState('');
    const [description, setDescription] =useState('');
    const [type, setType] =useState('');
const  [data, setData ] = useState([]);
const [collection, setCollection ] = useState([]);


const handleSubmi = (e) => {
    e.preventDefault();
    const form = {image, name, price, description, type};
}





useEffect(()=>{
  setData(PRODUCTS);
  setCollection([... new Set(PRODUCTS.map((product) => product.productType))])
}, [])


const products_filter = (productData) => {
  const filterData = PRODUCTS.filter((product)=> product.productType == productData);
  setData(filterData);

}
    

  return (
    <div className='foodsform'>
        <h2>Fileupload</h2>
        <form onSbmit= {handleSubmi} >
            <label>Hoolni ner</label>
            <input type="text" name="name"  onchange={handleInput}/>
            <label>Hoolni une</label>
            <input type="number" name="price" onchange={handleInput} />
            <label>Hoolni orts</label>
            <input type="text" name="description"onchange={handleInput} />
            <label>Hoolni zurag</label>
            <input type="file" name="image"onchange={handleInput} />
            <div>
            <label>Hoolni torol</label>
                <select type="text" name="type" onchange={handleInput} >
                    <option value="1-р хоол">1-р хоол</option>
                    <option value="2-р хоол">2-р хоол</option>
                    <option value="Уур зүйлс">3-р хоол </option>
                </select>
                            </div>
            <div>
                <button>add</button>
                <p>PRODUCTS:</p>
            </div>
        </form>
        <div>
            
            <button >edit</button>
            <button >del</button>
            <button >clear</button>
        </div>
    </div>
  )
}

export default Form
