import React from 'react'
import { Link } from 'react-router-dom';
// import style from "./logincss.css"

export const Validation =(values) =>  {
    alert("")
    let error = {}
    const email_pattern = /^[A-z][A-z0-9-_]{3,23}$/;
    const password_pattern = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/;

    if (values.name === "") {
        error.name= "Name should not be empy"
    } else{
        error.name=""
    }

    if (values.email === "") {
        error.email= "Email should not be empty"
    } 
    else if (!email_pattern.test(values.email)){
        error.email = "Email didnt match"
    } else {
        error.email =""
    }
    if (values.password === "") {
        error.password= "Password should not be empty"
    }
    else if (!password_pattern.test(values.email)){
        error.password = "Password didnt match"
    } else {
        error.password =""
    }
    return error;
}
